import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})

export class LoginComponent {
  constructor(private httpClient: HttpClient) {}

  login!: string;
  password!: string;

  data!: string;

  private url = 'https://api.nancydrew.me/authUser?key=X9ZO2Lqf&login=' + this.login + '&password=' + this.password

  onSubmit(){
    this.httpClient.get('https://api.nancydrew.me/authUser?key=X9ZO2Lqf&login=' + this.login + '&password=' + this.password)
  }
}
<div class="login-page">
    <div class="form-regis">
        <h4>Вход</h4>
        <input type="text" placeholder="Userame" class="text-input" id="nameInput">
        <input type="password" placeholder="Password" minlength="8" maxlength="20" class="text-input" id="passwordInput">
        <button type="button" class="btnRegister">Войти</button>
        <a class="sign" routerLink = "/regis"> Зарегистрироваться </a>
    </div>
  </div>
